{
  "context": "Quince harvesting process",
  "MTL_expressions": [
    {
      "id": "MTL_01",
      "description": "Manager checks the plantation everyday at 7am.",
      "propositions": [
        {
          "id": "p01",
          "description": "The manager checks the plantation",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        }
      ],
      "MTL_expression": "G_[0, 24h] (7:00 <= now -> X(p01))"
    },
    {
      "id": "MTL_02",
      "description": "If the plantation is affected by codling moth, the day's production is interrupted.",
      "propositions": [
        {
          "id": "p02",
          "description": "The plantation is affected by codling moth",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        },
        {
          "id": "p03",
          "description": "The day's production is interrupted",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        }
      ],
      "MTL_expression": "G_[0, 24h] (p02 -> p03)"
    },
    {
      "id": "MTL_03",
      "description": "The employees start picking fruits everyday at 8am.",
      "propositions": [
        {
          "id": "p04",
          "description": "The employees start picking fruits",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        }
      ],
      "MTL_expression": "G_[0, 24h] (8:00 <= now -> X(p04))"
    },
    {
      "id": "MTL_04",
      "description": "If the workers have not taken a break before 1pm, the manager reminds them to take a break soon.",
      "propositions": [
        {
          "id": "p05",
          "description": "Workers have taken a break",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        },
        {
          "id": "p06",
          "description": "Manager reminds them to take a break",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "day"
        }
      ],
      "MTL_expression": "G_[0, 24h] (13:00 <= now AND ¬p05 -> X(p06))"
    },
    {
      "id": "MTL_05",
      "description": "The trucks are loaded 7 hours after picking begins and continue till 5pm at most.",
      "propositions": [
        {
          "id": "p07",
          "description": "The trucks are loaded",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "hour"
        }
      ],
      "MTL_expression": "G_[0, 24h] [(p04 AND now <= 15:00) S (now >= (start_time(p04) + 7h) AND X(p07))]"
    },
    {
      "id": "MTL_06",
      "description": "The supervisor checks for spoiled fruit no later than 30 minutes after the trucks have been loaded.",
      "propositions": [
        {
          "id": "p08",
          "description": "The supervisor checks for spoiled fruit",
          "assumptions": [],
          "ambiguities": [],
          "granularity": "minute"
        }
      ],
      "MTL_expression": "G_[0, 24h] (now <= (start_time(p07) + 30min) -> X(p08))"
    }
  ],
  "dependency_sequence": [
    {
      "preceding_id": "MTL_01",
      "following_id": "MTL_02",
      "dependency_description": "'Manager checks the plantation' must happen before the codling moth check."
    },
    {
      "preceding_id": "MTL_02",
      "following_id": "MTL_03",
      "dependency_description": "'Workers start picking fruits' must occur after the codling moth check and potential production interruption."
    },
    {
      "preceding_id": "MTL_03",
      "following_id": "MTL_04",
      "dependency_description": "'Manager reminds workers to take a break' only occurs after they start picking the fruits."
    },
    {
      "preceding_id": "MTL_04",
      "following_id": "MTL_05",
      "dependency_description": "Loading the trucks is dependent on when the workers start picking fruits and whether they took a break."
    },
    {
      "preceding_id": "MTL_05",
      "following_id": "MTL_06",
      "dependency_description": "The supervisor's check for spoiled fruits occurs after the trucks have been loaded."
    }
  ],
  "non_formalized_parts": [
    {
      "id": "NF_01",
      "description": "In the evening, the supervisor reports the total number of kilos collected and the hours the employees have worked",
      "reason_for_non_formalization": "The specifics of the reported data (kilos collected, hours worked) aren't formally described. This aspect is more informational rather than affecting the sequence of events or dependencies."
    }
  ]
}
